import styled, {keyframes} from 'styled-components'

export const SApp = styled.div`
  text-align: left;
`



export const SHeader = styled.header`
  background-color: #282c34;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  font-size: calc(10px + 2vmin);
  color: white;
`
